val epsilon = 1
val e = 2.0
fun main(args: Array<String>) {

    val rsa = RSA()
    val keypair = rsa.generateKey(2048)

    val pubK = keypair.public
    val secK = keypair.private
    println(pubK)
    println(secK)
    val KeyAsInteger = BigInteger(secK.encoded)
    println("Secret and public key: ")
    println(pubK)
    println(secK)

    println("________________________________________________________________________________")
    println("SEcret Key as integer: ")
    println(KeyAsInteger)
 //   println(KeyAsInteger.bitLength())


    val rng =  Random(42)
    val field = BigInteger(9726, 1, rng)
    val shamir = Shamir()
    val shares : List<Shamir.Share> = shamir.shamirSecretSplitter(10, 5, field, secK.encoded)
    println("secret key in split shares: ")
    shares.forEach{ println(it)}


    var validInputGiven = false
    val read = Scanner(System.`in`)
    var x = 0
    println("________________________________________________________________________________")

    while (!validInputGiven) {
        println("input a 1 or 0: ")
        x = read.nextInt()

        if (x == 0 || x == 1)
            validInputGiven = true
    }
    println(x)
    val X = A(x)


    val cipher = rsa.encrypt(byteArrayOf(X.toByte()), pubK)
    println("input encrypted and diffused:")
    println(cipher)




